def quicksort(a, low, high):
  if low < high:

    pivot_index = partition(a, low, high)

    quicksort(a, low, pivot_index - 1)
    quicksort(a, pivot_index + 1, high)


def partition(a, low, high):
  pivot = a[low]
  i = low
  j = high
  while True:
    while a[i] <= pivot and i < high:
      i += 1

    while a[j] > pivot:
      j -= 1
    if i >= j:
      break
    a[i], a[j] = a[j], a[i]
  a[low], a[j] = a[j], a[low]
  return j

