def binarysearch(a,low,high,x):
  if low>high:
    return -1
  else:
    middle=(low+high)//2
    if a[middle]==x:
      return middle
    elif a[middle]>x:
      return binarysearch(a,low,middle-1,x)
    else:
      return binarysearch(a,middle+1,high,x)
