def check_costs(graph,costs,parents,a,status):
	node=graph[a]
	if node==None:
		status[a]=True
		return
	for i in node:
		new_cost=costs[a]+node[i]
		if new_cost<=costs[i]:
			costs[i]=new_cost
			parents[i]=a
	status[a]=True
	return


def compute(graph,costs,parents,status):
