int partition(int arr[],int low,int high){
	int pivot_element=arr[low];
	int i=low;
	int j=high;
	while (1){
		while (pivot_element>=arr[i] && i<high){
			i++;
		}
		while (pivot_element<arr[j]){
			j--;
		}
		if (i>=j){
			break;
		}
		swap(&arr[i],&arr[j]);
	}
	swap(&arr[j],&arr[low]);
	return j;
}

