void add_b(int a){
	basket* cur=(basket*) malloc(sizeof(basket));
	cur->data=a;
	cur->next=head;
	head=cur;
	if(tail==NULL){
		tail=head;
	}

}

void add_e(int a){
	basket* ptr =(basket*) malloc(sizeof(basket));
	ptr->data=a;
	if (head==NULL && tail==NULL){
		head=ptr;
		tail=ptr;
		return;
	}
	tail->next=ptr;
	tail=ptr;
}

void del(int a){
	basket* ptr=head;
	if (ptr==NULL){
		printf("List is emppty cannot perform del operation\\n");
	}
	while (ptr!=NULL){
		if (ptr->next->data==a){
			ptr->next=ptr->next->next;
			break;
		}
		ptr=ptr->next;
	}
}

