void merge(int arr[],int max[],int min[],int len_max,int len_min){
	int k=0,m=0;
	for (int i=0;i<len_min+len_max;i++){
		if (k>=len_min){
			k=len_min-1;
			min[k]=INT_MAX;
		}
		if (m>=len_max){
			m=len_max-1;
			max[m]=INT_MAX;
		}
		if (max[m]<min[k]){
			arr[i]=max[m];
			m++;
		}
		else{
			arr[i]=min[k];
			k++;
		}
	}
}

void mergesort(int arr[],int len){
	if (len>1){
		int mid=len/2;
		int a[mid];
		int b[len-mid];

		for (int i =0; i<mid; i++){
			a[i]=arr[i];
		}

		for (int i=mid; i<len;i++){
			b[i-mid]=arr[i];
		}

		mergesort(a,mid);
		mergesort(b,len-mid);

		int min[mid];
		int max[len-mid];

		for (int i=0; i<mid;i++){
			min[i]=a[i];
		}

		for (int i=0;i<len-mid;i++){
			max[i]=b[i];
		}

		if (mid>len-mid){
			merge(arr,min,max,mid,len-mid);
		}else{
			merge(arr,max,min,len-mid,mid);
		}

	}

}

int main(){
	int arr[]={2,3,1,0,45,23};
	mergesort(arr,6);
	for (int i=0; i<6;i++){
		printf(" %d",arr[i]);
	}
}

